import axios from "axios";
import { useEffect, useState } from "react";

function FreelancerCrud() {
    const [id, setId] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNo, setPhoneNo] = useState("");
    const [skillsets, setSkillsets] = useState("");
    const [hobby, setHobby] = useState("");
    const [freelancers, setUsers] = useState([]);

    useEffect(() => {
        (async () => await Load())();
    }, []);

    async function Load() {

        const result = await axios.get("https://localhost:7284/api/Freelancers/GetFreelancer");
        setUsers(result.data);
        console.log(result.data);
    }

    async function save(event) {

        event.preventDefault();
        try {
            await axios.post("https://localhost:7284/api/Freelancers/AddFreelancer", {

                username: username,
                email: email,
                phoneNo: phoneNo,
                skillsets: skillsets,
                hobby: hobby,

            });
            alert("Freelancer Registation Successfully");
            setId("");
            setUsername("");
            setEmail("");
            setPhoneNo("");
            setSkillsets("");
            setHobby("");


            Load();
        } catch (err) {
            alert(err);
        }
    }
    async function editFreelancer(freelancers) {
        setShowDiv(!showCreateUser);

        setUsername(freelancers.username);
        setEmail(freelancers.email);
        setPhoneNo(freelancers.phoneNo);
        setSkillsets(freelancers.skillsets);
        setHobby(freelancers.hobby);

        setId(freelancers.id);
    }

    async function DeleteFreelancer(id) {
        await axios.delete("https://localhost:7284/api/Freelancers/DeleteFreelancer/" + id);
        alert("Freelancer deleted Successfully");
        setId("");
        setUsername("");
        setEmail("");
        setPhoneNo("");
        setSkillsets("");
        setHobby("");
        Load();
    }

    async function update(event) {
        event.preventDefault();
        try {
            await axios.patch("https://localhost:7284/api/Freelancers/UpdateFreelancer/" + freelancers.find((u) => u.id === id).id || id,
                {
                    id: id,
                    username: username,
                    email: email,
                    phoneNo: phoneNo,
                    skillsets: skillsets,
                    hobby: hobby,
                }
            );
            alert("Registation Updated");
            setId("");
            setUsername("");
            setEmail("");
            setPhoneNo("");
            setSkillsets("");
            setHobby("");

            Load();
        } catch (err) {
            alert(err);
        }

    }

    //To hide or shown the details of freelancer
    const [showCreateUser, setShowDiv] = useState(false);

    const toggleDiv = () => {
        setShowDiv(!showCreateUser);
    };

    const hideDiv = () => {
        setShowDiv(!showCreateUser);
    };


    //To check is in editting mode
    const [isEditing, setIsEditing] = useState(false);

    const startEditing = () => {
        setIsEditing(true);
    };

    const cancelEditing = () => {
        setIsEditing(false);
    };

    return (
        <div>
            <div className="app-container">
                <div className="background-image">
                    {!showCreateUser && (
                        <div style={{width:'500px', height:'400px', border:'1px solid black', marginLeft:'70%'}}>
                            <p style={{  fontSize: '40px', marginTop: '40px' }}>New User?</p>
                            <p style={{  fontSize: '40px', marginTop: '-15px' }}>Register here</p>
                            <button className="create-user-button" onClick={toggleDiv}>
                                Create New User
                            </button>
                        </div>

                    )}
                    {showCreateUser && <div className="my-div">
                        <h1 style={{ textAlign: 'center' }}>Freelancer Details</h1>
                        <div className="container mt-4">
                            <form>
                                <div className="form-group">

                                    <input
                                        type="text"
                                        className="form-control"
                                        id="id"
                                        hidden
                                        value={id}
                                        onChange={(event) => {
                                            setId(event.target.value);
                                        }}
                                    />
                                    <label>Username</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        style={{ width: '350px' }}
                                        id="username"
                                        value={username}
                                        onChange={(event) => {
                                            setUsername(event.target.value);
                                        }}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Email</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        style={{ width: '350px' }}
                                        id="email"
                                        value={email}
                                        onChange={(event) => {
                                            setEmail(event.target.value);
                                        }}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Phone Number</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        style={{ width: '350px' }}
                                        id="phoneNo"
                                        value={phoneNo}
                                        onChange={(event) => {
                                            setPhoneNo(event.target.value);
                                        }}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Skill sets</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        style={{ width: '350px' }}
                                        id="skillsets"
                                        value={skillsets}
                                        onChange={(event) => {
                                            setSkillsets(event.target.value);
                                        }}
                                    />
                                </div>
                                <div className="form-group">
                                    <label>Hobby</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        style={{ width: '350px' }}
                                        id="hobby"
                                        value={hobby}
                                        onChange={(event) => {
                                            setHobby(event.target.value);
                                        }}
                                    />
                                </div>
                                <div>
                                    {isEditing ? (
                                        <button className="btn btn-warning mt-4" onClick={update}>
                                            Update
                                        </button>
                                    ) : (
                                        <button className="btn btn-primary mt-4" onClick={save} style={{ marginLeft: '20px' }}>
                                            Register
                                        </button>
                                    )}
                                    <button className="btn btn-secondary" onClick={hideDiv}>
                                        Cancel
                                    </button>
                                </div>
                            </form>
                        </div></div>}
                </div>

            </div>


            <br></br>
            <h2 style={{ textAlign: 'center', marginBottom: '30px' }}>List of Freelancers</h2>
            <table className="table table-dark" align="center">
                <thead>
                    <tr>
                        <th scope="col">Freelancer Id</th>
                        <th scope="col">Freelancer username</th>
                        <th scope="col">Email</th>
                        <th scope="col">Phone Number</th>
                        <th scope="col">Skill sets</th>
                        <th scope="col">Hobby</th>
                        <th scope="col">Option</th>
                    </tr>
                </thead>
                <tbody>
                    {freelancers.map(function fn(freelancer) {
                        return (
                            <tr key={freelancer.id}>
                                <th scope="row">{freelancer.id}</th>
                                <td>{freelancer.username}</td>
                                <td>{freelancer.email}</td>
                                <td>{freelancer.phoneNo}</td>
                                <td>{freelancer.skillsets}</td>
                                <td>{freelancer.hobby}</td>
                                <td>
                                    <button
                                        type="button"
                                        className="btn btn-warning"
                                        onClick={() => editFreelancer(freelancer)}
                                    >
                                        Edit
                                    </button>
                                    <button
                                        type="button"
                                        className="btn btn-danger"
                                        onClick={() => DeleteFreelancer(freelancer.id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>


        </div>
    );
}

export default FreelancerCrud;
