import axios from "axios";
import { useEffect, useState } from "react";
function FreelancerCrud() {
    const [id, setId] = useState("");
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [phoneNo, setPhoneNo] = useState("");
    const [skillsets, setSkillsets] = useState("");
    const [hobby, setHobby] = useState("");
    const [freelancers, setUsers] = useState([]);

    useEffect(() => {
        (async () => await Load())();
    }, []);

    async function Load() {

        const result = await axios.get("https://localhost:7284/api/Freelancers/GetFreelancer");
        setUsers(result.data);
        console.log(result.data);
    }

    async function save(event) {

        event.preventDefault();
        try {
            await axios.post("https://localhost:7284/api/Freelancers/AddFreelancer", {

                username: username,
                email: email,
                phoneNo: phoneNo,
                skillsets: skillsets,
                hobby: hobby,

            });
            alert("Freelancer Registation Successfully");
            setId("");
            setUsername("");
            setEmail("");
            setPhoneNo("");
            setSkillsets("");
            setHobby("");


            Load();
        } catch (err) {
            alert(err);
        }
    }
    async function editFreelancer(freelancers) {
        setUsername(freelancers.username);
        setEmail(freelancers.email);
        setPhoneNo(freelancers.phoneNo);
        setSkillsets(freelancers.skillsets);
        setHobby(freelancers.hobby);


        setId(freelancers.id);
    }

    async function DeleteFreelancer(id) {
        await axios.delete("https://localhost:7284/api/Freelancers/DeleteFreelancer/" + id);
        alert("Freelancer deleted Successfully");
        setId("");
        setUsername("");
        setEmail("");
        setPhoneNo("");
        setSkillsets("");
        setHobby("");
        Load();
    }

    async function update(event) {
        event.preventDefault();
        try {
            await axios.patch("https://localhost:7284/api/Freelancers/UpdateFreelancer/" + freelancers.find((u) => u.id === id).id || id,
                {
                    id: id,
                    username: username,
                    email: email,
                    phoneNo: phoneNo,
                    skillsets: skillsets,
                    hobby: hobby,
                }
            );
            alert("Registation Updated");
            setId("");
            setUsername("");
            setEmail("");
            setPhoneNo("");
            setSkillsets("");
            setHobby("");

            Load();
        } catch (err) {
            alert(err);
        }

        // const [showDiv, setShowDiv] = useState(false);
        
        const toggleDiv = () => {
            setShowDiv(!showDiv);
  };
    }
    return (
        <div>
             {/* <button onClick={toggleDiv}>Toggle Div</button>
      {showDiv && <div className="my-div">This is the hidden content.</div>} */}
            <h1>Freelancer Details</h1>
            <div className="container mt-4">
                <form>
                    <div className="form-group">

                        <input
                            type="text"
                            className="form-control"
                            id="id"
                            hidden
                            value={id}
                            onChange={(event) => {
                                setId(event.target.value);
                            }}
                        />
                        <label>Freelancer username</label>
                        <input
                            type="text"
                            className="form-control"
                            id="username"
                            value={username}
                            onChange={(event) => {
                                setUsername(event.target.value);
                            }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Email</label>
                        <input
                            type="text"
                            className="form-control"
                            id="email"
                            value={email}
                            onChange={(event) => {
                                setEmail(event.target.value);
                            }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Phone Number</label>
                        <input
                            type="text"
                            className="form-control"
                            id="phoneNo"
                            value={phoneNo}
                            onChange={(event) => {
                                setPhoneNo(event.target.value);
                            }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Skill sets</label>
                        <input
                            type="text"
                            className="form-control"
                            id="skillsets"
                            value={skillsets}
                            onChange={(event) => {
                                setSkillsets(event.target.value);
                            }}
                        />
                    </div>
                    <div className="form-group">
                        <label>Hobby</label>
                        <input
                            type="text"
                            className="form-control"
                            id="hobby"
                            value={hobby}
                            onChange={(event) => {
                                setHobby(event.target.value);
                            }}
                        />
                    </div>
                    <div>
                        <button className="btn btn-primary mt-4" onClick={save}>
                            Register
                        </button>
                        <button className="btn btn-warning mt-4" onClick={update}>
                            Update
                        </button>
                    </div>
                </form>
            </div>
            <br></br>
            <table className="table table-dark" align="center">
                <thead>
                    <tr>
                        <th scope="col">Freelancer Id</th>
                        <th scope="col">Freelancer username</th>
                        <th scope="col">Email</th>
                        <th scope="col">Phone Number</th>
                        <th scope="col">Skill sets</th>
                        <th scope="col">Hobby</th>
                        <th scope="col">Option</th>
                    </tr>
                </thead>
                <tbody>
                    {freelancers.map(function fn(freelancer) {
                        return (
                            <tr key={freelancer.id}>
                                <th scope="row">{freelancer.id}</th>
                                <td>{freelancer.username}</td>
                                <td>{freelancer.email}</td>
                                <td>{freelancer.phoneNo}</td>
                                <td>{freelancer.skillsets}</td>
                                <td>{freelancer.hobby}</td>
                                <td>
                                    <button
                                        type="button"
                                        className="btn btn-warning"
                                        onClick={() => editFreelancer(freelancer)}
                                    >
                                        Edit
                                    </button>
                                    <button
                                        type="button"
                                        className="btn btn-danger"
                                        onClick={() => DeleteFreelancer(freelancer.id)}
                                    >
                                        Delete
                                    </button>
                                </td>
                            </tr>
                        );
                    })}
                </tbody>
            </table>


        </div>
    );
}

export default FreelancerCrud;
